mapper.insert:
INSERT INTO books (
    title, writer, pub, sal, created_at, comm, deptno
) VALUES (
    #{title}, #{writer}, #{pub}, #{sal}, #{created_at}, #{comm}, #{deptno}
)

mapper.update:
UPDATE books SET
    title=#{title}, writer=#{writer}, pub=#{pub}, sal=#{sal}, created_at=#{created_at}, comm=#{comm}, deptno=#{deptno}
WHERE id=#{id}
